package com.example.e_business.utility.module;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.example.e_business.R;import com.squareup.picasso.Picasso;import java.io.IOException;import java.util.List;public class GridAdapter  extends BaseAdapter {    Context context;    List<ItemsUpload> itemsUpload;    public GridAdapter(Context context, List<ItemsUpload> itemsUploaded) {        this.context = context;        this.itemsUpload = itemsUploaded;    }    @Override    public int getCount()    {        return itemsUpload.size();    }    @Override    public Object getItem(int i) {        return null;    }    @Override    public long getItemId(int i) {        return 0;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null)        {           convertView = LayoutInflater.from(context).inflate(R.layout.gird_items,parent,false);        }        ItemsUpload itemsUploads = itemsUpload.get(position);        ImageView img = convertView.findViewById(R.id.image_view_sample);        TextView textView = convertView.findViewById(R.id.pro_name_sample);        TextView txtViewPr = convertView.findViewById(R.id.pro_prc_sample);        TextView textView3 = convertView.findViewById(R.id.pro_d_sample);        Picasso.get()                .load(itemsUploads.getImageUri())                .placeholder(android.R.drawable.ic_menu_upload)                .fit()                .centerCrop()                .into(img);        textView.setText(itemsUploads.getProduct_Name());        txtViewPr.setText(itemsUploads.getProduct_Price());        return convertView;    }}