package com.example.e_business.utility.ui;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.util.Patterns;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.ProgressBar;import android.widget.TextView;import android.widget.Toast;import com.example.e_business.MainActivity;import com.example.e_business.R;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;import org.jetbrains.annotations.NotNull;public class LoginActivity extends AppCompatActivity implements View.OnClickListener {    private EditText emailEditText_login,passwordEditText_login;    private Button login_btn;    private TextView txtView_login;    private ProgressBar progressBar_login;    private FirebaseAuth mAuth;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        emailEditText_login = findViewById(R.id.email_login);        passwordEditText_login = findViewById(R.id.password_login);        login_btn = findViewById(R.id.Login_btn);        txtView_login = findViewById(R.id.txtView_login);        mAuth = FirebaseAuth.getInstance();        progressBar_login = findViewById(R.id.progressBar_login);        login_btn.setOnClickListener(this);        txtView_login.setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()){            case R.id.Login_btn:                userLogin();                break;            case R.id.txtView_login:                Intent intent = new Intent(getApplicationContext(),SingUpActivity.class);               startActivity(intent);        }    }    private void userLogin() {        String email = emailEditText_login.getText().toString().trim();        String password = passwordEditText_login.getText().toString().trim();        if(email.isEmpty())        {            emailEditText_login . setError("Enter an Email address");            emailEditText_login.requestFocus();            return;        }        if(!Patterns.EMAIL_ADDRESS.matcher(email).matches())        {            emailEditText_login.setError("Email or Gmail Not Match ");            emailEditText_login.requestFocus();            return;        }        if(password.isEmpty()){            passwordEditText_login.setError("Enter a Password");            passwordEditText_login.requestFocus();            return;        }        if(password.length()<6){            passwordEditText_login.setError("Minimum Six Character");            passwordEditText_login.requestFocus();            return;        }        mAuth.createUserWithEmailAndPassword(email,password).addOnCompleteListener(new OnCompleteListener<AuthResult>() {            @Override            public void onComplete(@NonNull @NotNull Task<AuthResult> task) {                if (task.isSuccessful()){                    finish ();                    startActivity ( new Intent ( getApplicationContext (), MainActivity.class ) );                    Intent intent = new Intent ();                    intent.addFlags ( Intent.FLAG_ACTIVITY_CLEAR_TOP );                }else {                    Toast.makeText(getApplicationContext(),"Login is Not successful",Toast.LENGTH_SHORT).show();                }            }        });    }}